import React, {useState} from 'react'; //useState hook allows functional components to manage state.

const App = (props)=> { // Define a functional component named App which accepts props.
  const [counter, setCounter] = useState(0); // Declare a state variable named counter initialized to 0 using the useState hook.
  //The current state value (counter).
  //The primary function provided by React to update the state (setCounter).

  let incrementCounter = ()=> {
    setCounter(counter+1);
  }
  let colorStyle = {color:props['color'],fontSize:props['size']}
    return (
      <div style={colorStyle}>
        React Component
        <br /><br />
        <button onClick={incrementCounter}>Click Me!</button>
        <br /><br />
        {counter}
      </div>
    );
  }

  export default App;